import React from "react";
import { useState } from "react";
import Portfolio from "../portfolio_categories";
import { Link } from "react-router-dom";


const GalleryReact = () => {

    const [items, setItems] = useState(Portfolio);

   let a = null;
   
    const filterItem = (categortItem) => {
      

        const updatedItems = Portfolio.filter((currentElem) => {
            //return currentElem.category === categortItem;

            if(currentElem.category === categortItem) {
                a = true;
            }else{
                a = false;
            }
            return a;
        });

        console.log(a);
     
       //setItems(updatedItems);

        
       

    }
    return (
        <>
        <p>Our Latest Featured Projects</p>

        <div className="menu-tabs container">
            <div className="menu-tab d-flex justify-content-around">
            <button className="btn btn-warning" onClick={() => filterItem('All')}>All</button>
                <button className="btn btn-warning" onClick={() => filterItem('Wordpress')}>Wordpress</button>
                <button className="btn btn-warning" onClick={() => filterItem('Drupal')}>Drupal</button>
                <button className="btn btn-warning" onClick={() => filterItem('Codeigniter')}>Codeigniter</button>
                <button className="btn btn-warning" onClick={() => filterItem('Magento')}>Magento</button>
                <button className="btn btn-warning" onClick={() => filterItem('ExpressionEngine')}>ExpressionEngine</button>

            </div>
        </div>

        <div className="">
            <div className="row">
                <div className="col-11 mx-auto">
                    <div className="row">
                        {
                            items.map((elem) => {
                                const {id, name, image, description, link} = elem;

                                return (
                                    <div class="col-lg-4 col-md-4 col-sm-6 brand manipul design print">
        				<div class="h_gallery_item">
        					<div class="g_img_item">
        						<img class="img-fluid" src={image} alt="" />
        						
        					</div>
        					<div class="g_item_text">
        						<h4>{name}</h4>
        						<p>{description}</p>
                                <Link to={link} target="_blank"><button>Open link</button></Link>
        					</div>
        				</div>
        			</div>
                                )

                            })
                        }

                   
                       

                        

                    </div>
                </div>

            </div>
        </div>
        </>
        
      )
}


export default GalleryReact;
