import React from "react";
import { useState } from "react";
import Portfolio from "../portfolio_categories";
import { Link } from "react-router-dom";


const GalleryReact = () => {

    const [items, setItems] = useState(Portfolio);

    const filterItem = (categortItem) => {
        alert(categortItem);

    }
    return (
        <>
        <p>Our Latest Featured Projects</p>

        <div className="menu-tabs container">
            <div className="menu-tab d-flex justify-content-around">
                <button className="btn btn-warning" onClick={() => filterItem('php')}>PHP</button>
                <button className="btn btn-warning">React</button>
                <button className="btn btn-warning">Wordpress</button>
                <button className="btn btn-warning">Drupal</button>
                <button className="btn btn-warning">ExpressionEngine</button>

            </div>
        </div>

        <div className="">
            <div className="row">
                <div className="col-11 mx-auto">
                    <div className="row">
                        {
                            items.map((elem) => {
                                const {id, name, image, description, link} = elem;

                                return (
                                    <div class="col-lg-4 col-md-4 col-sm-6 brand manipul design print">
        				<div class="h_gallery_item">
        					<div class="g_img_item">
        						<img class="img-fluid" src={image} alt="" />
        						<img src="img/gallery/icon.png" alt="" />
        					</div>
        					<div class="g_item_text">
        						<h4>{name}</h4>
        						<p>{description}</p>
                                <Link to={link}>Link</Link>
        					</div>
        				</div>
        			</div>
                                )

                            })
                        }

                   
                       

                        

                    </div>
                </div>

            </div>
        </div>
        </>
        
      )
}


export default GalleryReact;
